#!/usr/bin/env ruby

# http://adventofcode.com/2017/day/1
#
# "find the sum of all digits that match the next digit in the list.
# The list is circular, so the digit after the last digit is the first digit in the list."

inputs = <<~EOF.lines
  1122
  1111
  1234
  91212129
  
EOF

def solve(str)
  digits = str.chars.map(&:to_i)
  (0..digits.length - 1).inject(0) do |sum, i|
    this_digit = digits[i]
    next_digit = i == digits.length - 1 ? digits[0] : digits[i + 1]
    this_digit == next_digit ? sum + this_digit : sum
  end
end

inputs.each do |input|
  puts solve(input)
end
